<?php

require_once dirname(__FILE__) . '/../../../../library/GMH/Septa/Route.php';

/**
 * Test class for GMH_Septa_Route.
 * Generated by PHPUnit on 2012-11-18 at 07:34:57.
 */
class GMH_Septa_RouteTest extends PHPUnit_Framework_TestCase {

    /**
     * @var GMH_Septa_Route
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        $this->object = new GMH_Septa_Route;
    }

    public function testTripExists() {
        $tripId = "123";

        $trip = new GMH_Septa_Trip();
        $trip->setTripId($tripId);
        $this->object->addTrip($trip);

        $trip = $this->object->getTripById($tripId);

        $this->assertEquals($tripId, $trip->getTripId());
    }

    public function testGetTripByStopTime() {
        
        $directionId = "0";
        $departureTime = "7:00:00";
        
        $stopTime = new GMH_Septa_StopTime();
        $stopTime->setDepartureTime("7:00:00");
        
        $trip = new GMH_Septa_Trip();
        $trip->addStopTime($stopTime);
        $trip->setDirectionId($directionId);
        $this->object->addTrip($trip);
        
        $result = $this->object->getTripByStopTime($departureTime, $directionId);
        
        $this->assertNotNull( $result );
        $this->assertEquals( $result->getDirectionId(), $directionId );
    }
}

?>
